const sfi = parseFloat(msg.payload.solar.solardata[0].solarflux[0]);
const kdx = parseFloat(msg.payload.solar.solardata[0].kindex[0]);
const Aindex = parseFloat(msg.payload.solar.solardata[0].aindex);
const muf = msg.payload.solar.solardata[0].muf[0]; 
const fof2 = (parseFloat(muf / 3.084).toFixed(2) !== 'Nan') ? parseFloat(muf / 3.084).toFixed(2) : 0;

let sficolor
if (sfi <= 80) { sficolor = '#ff0000' }
if (sfi > 80 && sfi <= 120) { sficolor = '#ffcc00' }
if (sfi > 130) { sficolor = "#14f00c" }

let aColor
if (Aindex <= 3) { aColor = 'blue' }
if (Aindex > 3 && Aindex <= 27) { aColor = "#14f00c" }
if (Aindex > 27 && Aindex <= 80) { aColor = "yellow" }
if (Aindex > 80) { aColor = "red" }

let kColor
if (kdx <= 1) { kColor = '#d7e8fa' }
if (kdx > 1 && kdx <= 4) { kColor = "#14f00c" }
if (kdx > 4 && kdx <= 6) { kColor = "yellow" }
if (kdx > 6) { kColor = "red" }

msg = { payload: 0, topic: 'solar', sfi: sfi, sficolor: sficolor, kdx: kdx, Aindex: Aindex, aColor: aColor, kColor: kColor };
return msg;

const tempUnit = flow.get("tempUnit");
const distanceUnit = flow.get("distance_unit");

// Retrieve stored values from context
const tempc = flow.get("tempc") || 0;
const windSpeed = flow.get("windSpeed") || 0;
const wDeg = flow.get("windDirection1") || 0;
const pressure = msg.payload.pressure || 0; // Assuming pressure comes in the msg.payload;


// Convert temperature to Fahrenheit if needed
const temp = tempUnit === 'f' ? Math.round((tempc * 9 / 5) + 32) : tempc;

// Convert wind speed to desired unit
const wSpeed = distanceUnit === 'mi' ? Math.round(windSpeed) : Math.round(windSpeed * 1.609344);
const spdUnit = distanceUnit === 'mi' ? 'mph' : 'kmh';

// Determine temperature icon color
let tIcon;
if (tempUnit === 'f') {
    if (temp < 30) {
        tIcon = '#00ffff';
    } else if (temp < 50) {
        tIcon = '#6699ff';
    } else if (temp < 70) {
        tIcon = '#00cc00';
    } else if (temp < 90) {
        tIcon = '#ff9900';
    } else {
        tIcon = '#ff6600';
    }
} else if (tempUnit === 'c') {
    if (temp < -1) {
        tIcon = '#00ffff';
    } else if (temp < 10) {
        tIcon = '#6699ff';
    } else if (temp < 21) {
        tIcon = '#00cc00';
    } else if (temp < 32) {
        tIcon = '#ff9900';
    } else {
        tIcon = '#ff6600';
    }
}

// Determine pressure trend
let pBar;
let pBar1;
const wBar = flow.get("wBar") || 0;

if (wBar > pressure) {
    pBar = "<i class='material-icons trending_up'></i>";
    pBar1 = 'trending_up';
} else if (wBar < pressure) {
    pBar = "<i class='material-icons trending_down'></i>";
    pBar1 = 'trending_down';
} else {
    pBar = "<i class='material-icons trending_flat'></i>";
    pBar1 = 'trending_flat';
}

flow.set('wBar', pressure);

let wFrom;
if (wDeg >= 348.75 && wDeg <= 360) {
    wFrom = "<i class='wi wi-direction wi-from-N wi-1x\'</i>";
}
if (wDeg >= 0 && wDeg < 11.25) {
    wFrom = "<i class='wi wi-direction wi-from-N wi-1x\'</i>";
}
if (wDeg >= 11.25 && wDeg <= 33.75) {
    wFrom = "<i class='wi wi-direction wi-from-NNE wi-1x\'</i>";
}
if (wDeg > 33.75 && wDeg <= 56.25) {
    wFrom = "<i class='wi wi-direction wi-from-NE wi-1x\'</i>";
}
if (wDeg > 56.25 && wDeg <= 78.75) {
    wFrom = "<i class='wi wi-direction wi-from-ENE wi-1x\'</i>";
}
if (wDeg > 78.75 && wDeg <= 101.25) {
    wFrom = "<i class='wi wi-direction wi-from-E wi-1x\'</i>";
}
if (wDeg > 101.25 && wDeg <= 123.75) {
    wFrom = "<i class='wi wi-direction wi-from-ESE wi-1x\'</i>";
}
if (wDeg > 123.75 && wDeg <= 146.25) {
    wFrom = "<i class='wi wi-direction wi-from-SE wi-1x\'</i>";
}
if (wDeg > 146.25 && wDeg <= 168.75) {
    wFrom = "<i class='wi wi-direction wi-from-SSE wi-1x\'</i>";
}
if (wDeg > 168.75 && wDeg <= 191.25) {
    wFrom = "<i class='wi wi-direction wi-from-S wi-1x\'</i>";
}
if (wDeg > 191.25 && wDeg <= 213.75) {
    wFrom = "<i class='wi wi-direction wi-from-SSW wi-1x\'</i>";
}
if (wDeg > 213.75 && wDeg <= 236.25) {
    wFrom = "<i class='wi wi-direction wi-from-SW wi-1x\'</i>";
}
if (wDeg > 236.25 && wDeg <= 258.75) {
    wFrom = "<i class='wi wi-direction wi-from-WSW wi-1x\'</i>";
}
if (wDeg > 258.75 && wDeg <= 281.25) {
    wFrom = "<i class='wi wi-direction wi-from-W wi-1x\'</i>";
}
if (wDeg > 281.25 && wDeg <= 303.75) {
    wFrom = "<i class='wi wi-direction wi-from-WNW wi-1x\'</i>";
}
if (wDeg > 303.75 && wDeg <= 326.25) {
    wFrom = "<i class='wi wi-direction wi-from-NW wi-1x\'</i>";
}
if (wDeg > 326.25 && wDeg <= 348.75) {
    wFrom = "<i class='wi wi-direction wi-from-NNW wi-1x\'</i>";
}
// Set message properties
msg.payload = 0;
msg.topic = 'wx';
msg.temp = temp;
msg.tIcon = tIcon;
msg.windir = wFrom;
msg.speed = wSpeed;
msg.spdUnit = spdUnit;
msg.wxicon = '<i class=\'wi wi-owm-' + msg.data.weather[0].icon + ' wi-1x\' ></i>'; // Assuming icon comes in the msg.payload
msg.bar = pBar;
msg.bar1 = pBar1;
msg.wBar = wBar;

return msg;
